package com.example.wiprotest.service.instruments;import com.example.wiprotest.model.CalculatedPrice;import com.example.wiprotest.model.InstrumentPriceHistory;import com.example.wiprotest.service.InstrumentPriceCalculator;import java.util.List;import static com.example.wiprotest.utils.ListUtils.checkNotEmpty;/** * SPEC: mean */public class Instrument1PriceCalculator implements InstrumentPriceCalculator {	@Override	public CalculatedPrice calculate(List<InstrumentPriceHistory> history) {		checkNotEmpty(history);		return new CalculatedPrice(			history.get(0).name(),			history.stream().mapToDouble(InstrumentPriceHistory::value).summaryStatistics().getAverage()		);	}}