package com.example.wiprotest.service.instruments;import com.example.wiprotest.model.CalculatedPrice;import com.example.wiprotest.model.InstrumentPriceHistory;import com.example.wiprotest.service.InstrumentPriceCalculator;import java.math.BigDecimal;import java.math.RoundingMode;import java.util.List;import java.util.stream.Collectors;import static com.example.wiprotest.utils.ListUtils.checkNotEmpty;/** * SPEC: mean */public class Instrument1PriceCalculator implements InstrumentPriceCalculator {	private Double sum = 0d;	private long counter = 0;	@Override	public void consume(InstrumentPriceHistory history) {		sum += history.value();		counter++;	}	@Override	public CalculatedPrice calculate() {		return new CalculatedPrice("INSTRUMENT1", sum / counter);	}}