package com.example.wiprotest.utils;import java.text.ParseException;import java.text.SimpleDateFormat;import java.util.Date;public class DateParser {	private static final ThreadLocal<SimpleDateFormat> DUMP_DATE_FORMAT = new ThreadLocal<>() {		@Override		protected SimpleDateFormat initialValue() {			return new SimpleDateFormat("dd-MMM-yyyy");		}	};	public static Date parseDumpDate(String date) throws ParseException {		return DUMP_DATE_FORMAT.get().parse(date);	}	public static String formatDumpDate(Date date) {		return DUMP_DATE_FORMAT.get().format(date);	}}