package com.example.wiprotest.utils;import java.util.Comparator;import java.util.List;import java.util.stream.Collectors;public class ListUtils {	public static <T> List<T> reduceList(List<T> list, int maxItems, Comparator<T> comparator) {		return list.stream().sorted(comparator).limit(maxItems).collect(Collectors.toList());	}}